mapper.select:
select max(sort)
FROM rdm_object_attr
WHERE object_id = #{value}

select count(1)
from rdm_object_attr
where object_id = #{objectId}
  and id != #{id}
  and name = #{name}

select id, object_id as objectId, type, name, label, description, is_required AS isRequired, sort, is_private AS isPrivate, config AS configStr, is_active AS isActive FROM rdm_object_attr where object_id = #{objectId}
and is_active = #{isActive}
order by sort

SELECT count(1)
from rdm_project
WHERE name = #{name}
  AND id != #{id}

where.if:
AND (name LIKE concat('%', #{keyword}, '%')
or description LIKE concat('%',#{keyword},'%')
)

mapper.update:
update rdm_object_attr
set is_active = #{isActive}
WHERE id = #{id}

update rdm_object_attr
set sort = #{sort}
where id = #{id}
