mapper.select:
SELECT
COUNT(id)
FROM `rdm_project_priority` a
WHERE project_uuid = #{projectUuid}
AND process_area_uuid = #{processAreaUuid}

    AND a.name LIKE CONCAT('%', #{keyword}, '%')

SELECT
COUNT(id)
FROM `rdm_project_priority` a
WHERE project_uuid = #{projectUuid}
AND process_area_uuid = #{processAreaUuid}
AND a.name = #{name}

    AND a.uuid != #{uuid}

SELECT `id`, `name`, `uuid`, `color`, `is_default` as `isDefault`, DATE_FORMAT(a.create_time, '%Y-%m-%d %H:%i:%s') AS createTime, `create_user` AS createUser, DATE_FORMAT(a.update_time, '%Y-%m-%d %H:%i:%s') AS updateTime, `update_user` AS updateUser FROM `rdm_project_priority` a WHERE project_uuid = #{projectUuid} AND process_area_uuid = #{processAreaUuid}
AND a.name LIKE CONCAT('%', #{keyword}, '%')
ORDER BY a.id
LIMIT #{startNum}, #{pageSize}

mapper.delete:
DELETE FROM `rdm_project_priority`
WHERE project_uuid = #{projectUuid}
AND process_area_uuid = #{processAreaUuid}
AND uuid = #{uuid}
