mapper.select:
select count(distinct ri.id) as issueCount, ra.id as id, ra.app_type as type, ra.sort as sort from rdm_issue ri join rdm_app ra on ri.app_id = ra.id left join rdm_issue_user riu on ri.id = riu.issue_id
ra.project_id = #{projectId}
and riu.user_id = #{userId}
group by ra.id order by ra.sort

select id,
       app_id         as appId,
       from_status_id as fromStatusId,
       to_status_id   as toStatusId,
       config         as configStr
from rdm_app_status_rel
where app_id = #{appId}
  and from_status_id = #{fromStatusId}
  and to_status_id = #{toStatusId}

select a.id, a.app_id as appId, a.is_start as isStart, a.is_end as isEnd, a.name, a.label, a.sort, a.color FROM rdm_app_status a
join rdm_app_status_rel b on a.id = b.to_status_id and b.from_status_id = #{status} and b.app_id = #{appId}
WHERE a.app_id = #{appId} order by a.sort

select user_id as userId,
       app_id  as appId,
       config  as configStr
from rdm_app_usersetting
where user_id = #{userId}
  and app_id = #{appId}

mapper.update:
update rdm_app_status_rel
set config = #{configStr}
where id = #{id}

update rdm_app_status
set is_start = 0
where app_id = #{value}

update rdm_app_status
set id = #{id}

    ,is_start = #{isStart}


    ,is_end = #{isEnd}

where id = #{id}

mapper.delete:
delete
from rdm_app_status_rel
where app_id = #{appId}
  and from_status_id = #{fromStatusId}
  and to_status_id = #{toStatusId}
