mapper.select:
select id, app_id as appId, from_status_id as fromStatusId, to_status_id as toStatusId
from rdm_app_status_rel
where app_id = #{appId}
  and from_status_id = #{fromStatusId}
  and to_status_id = #{toStatusId}

select count(1)
from rdm_app_catalog
where app_id = #{appId}
  and name = #{name}
  and id != #{id}

select max(sort)
FROM rdm_app_attr
WHERE app_id = #{value}

select a.id, a.app_id as appId, a.is_start as isStart, a.is_end as isEnd, a.name, a.label, a.sort, a.color FROM rdm_app_status a
join rdm_app_status_rel b on a.id = b.to_status_id and b.from_status_id = #{status} and b.app_id = #{appId}
WHERE a.app_id = #{appId} order by a.sort

select id, app_id as objectId, type, name, label, description, is_required AS isRequired, sort, is_private AS isPrivate, config AS configStr, is_active AS isActive FROM rdm_app_attr where app_id = #{appId}
and is_active = #{isActive}
order by sort

mapper.update:
update rdm_app_catalog
set parent_id = #{parentId}
where id = #{id}

update rdm_app_catalog
set lft = #{lft}
where id = #{id}

update rdm_app_attr
set sort = #{sort}
where id = #{id}

update rdm_app_attr
set is_active = #{isActive}
WHERE id = #{id}

update rdm_app_catalog
set name = #{name}
where id = #{id}

update rdm_app_status_rel
set config = #{configStr}
where id = #{id}

update rdm_app_status
set is_start = 0
where app_id = #{value}

update rdm_app_status
set id = #{id}

    ,is_start = #{isStart}


    ,is_end = #{isEnd}

where id = #{id}

mapper.delete:
delete
from rdm_app_status_rel
where app_id = #{appId}
  and from_status_id = #{fromStatusId}
  and to_status_id = #{toStatusId}
