mapper.select:
select count(1)
from rdm_app_attr
where app_id = #{appId}
  and id != #{id}
  and is_private = #{isPrivate}
  and name = #{name}

select max(sort)
FROM rdm_app_attr
WHERE app_id = #{value}

select id, app_id as appId, type, name, label, description, is_required AS isRequired, sort, is_private AS isPrivate, config AS configStr, is_active AS isActive FROM rdm_app_attr where app_id = #{appId}
and is_active = #{isActive}
order by sort

mapper.update:
update rdm_app_attr
set name  = #{name},
    label = #{label}
where id = #{id}

update rdm_app_attr
set sort = #{sort}
where id = #{id}

update rdm_app_attr
set is_active = #{isActive}
WHERE id = #{id}

update rdm_app_attr
set is_required = #{isRequired}
WHERE id = #{id}
